WHEN (mt4_trades.`GROUP` IN (SELECT `GROUP` FROM live5.group_table WHERE BOOK = 'A' AND CURRENCY = 'USD') AND mt4_trades.CLOSE_TIME = '1970-01-01 00:00:00') THEN mt4_trades.PROFIT+mt4_trades.SWAPS 
