WHEN (mt4_trades.`GROUP` IN (SELECT `GROUP` FROM live5.group_table WHERE BOOK = 'A' AND CURRENCY = 'GBP') AND mt4_trades.CLOSE_TIME = '1970-01-01 00:00:00') THEN (mt4_trades.PROFIT+mt4_trades.SWAPS)*(SELECT AVERAGE FROM daily_prices WHERE SYMBOL LIKE 'GBPUSD' ORDER BY TIME DESC LIMIT 1) 
WHEN (mt4_trades.`GROUP` IN (SELECT `GROUP` FROM live5.group_table WHERE BOOK = 'A' AND CURRENCY = 'NZD') AND mt4_trades.CLOSE_TIME = '1970-01-01 00:00:00') THEN (mt4_trades.PROFIT+mt4_trades.SWAPS)*(SELECT AVERAGE FROM daily_prices WHERE SYMBOL LIKE 'NZDUSD' ORDER BY TIME DESC LIMIT 1) 
WHEN (mt4_trades.`GROUP` IN (SELECT `GROUP` FROM live5.group_table WHERE BOOK = 'A' AND CURRENCY = 'AUD') AND mt4_trades.CLOSE_TIME = '1970-01-01 00:00:00') THEN (mt4_trades.PROFIT+mt4_trades.SWAPS)*(SELECT AVERAGE FROM daily_prices WHERE SYMBOL LIKE 'AUDUSD' ORDER BY TIME DESC LIMIT 1) 
WHEN (mt4_trades.`GROUP` IN (SELECT `GROUP` FROM live5.group_table WHERE BOOK = 'A' AND CURRENCY = 'SGD') AND mt4_trades.CLOSE_TIME = '1970-01-01 00:00:00') THEN (mt4_trades.PROFIT+mt4_trades.SWAPS)/(SELECT AVERAGE FROM daily_prices WHERE SYMBOL LIKE 'USDSGD' ORDER BY TIME DESC LIMIT 1) ELSE 0 END),2) AS PROFIT,
ROUND(SUM(CASE 
WHEN (mt4_trades.`GROUP` IN (SELECT `GROUP` FROM live5.group_table WHERE BOOK = 'A' AND CURRENCY = 'USD') AND (mt4_trades.CLOSE_TIME > DATE_ADD((CASE WHEN HOUR(DATE_SUB(NOW(),INTERVAL (SELECT SERVER_TIME_DIFF FROM live5.live1_time_diff) HOUR)) < 23 THEN DATE_FORMAT(DATE_SUB(DATE_SUB(NOW(),INTERVAL (SELECT SERVER_TIME_DIFF FROM live5.live1_time_diff) HOUR),INTERVAL 1 DAY),'%Y-%m-%d 23:00:00') ELSE DATE_FORMAT(DATE_SUB(NOW(),INTERVAL (SELECT SERVER_TIME_DIFF FROM live5.live1_time_diff) HOUR),'%Y-%m-%d 23:00:00') END),INTERVAL 1 HOUR))) THEN mt4_trades.PROFIT+mt4_trades.SWAPS 
WHEN (mt4_trades.`GROUP` IN (SELECT `GROUP` FROM live5.group_table WHERE BOOK = 'A' AND CURRENCY = 'EUR') AND (mt4_trades.CLOSE_TIME > DATE_ADD((CASE WHEN HOUR(DATE_SUB(NOW(),INTERVAL (SELECT SERVER_TIME_DIFF FROM live5.live1_time_diff) HOUR)) < 23 THEN DATE_FORMAT(DATE_SUB(DATE_SUB(NOW(),INTERVAL (SELECT SERVER_TIME_DIFF FROM live5.live1_time_diff) HOUR),INTERVAL 1 DAY),'%Y-%m-%d 23:00:00') ELSE DATE_FORMAT(DATE_SUB(NOW(),INTERVAL (SELECT SERVER_TIME_DIFF FROM live5.live1_time_diff) HOUR),'%Y-%m-%d 23:00:00') END),INTERVAL 1 HOUR))) THEN (mt4_trades.PROFIT+mt4_trades.SWAPS)*(SELECT AVERAGE FROM daily_prices WHERE SYMBOL LIKE 'EURUSD' ORDER BY TIME DESC LIMIT 1) 
WHEN (mt4_trades.`GROUP` IN (SELECT `GROUP` FROM live5.group_table WHERE BOOK = 'A' AND CURRENCY = 'GBP') AND (mt4_trades.CLOSE_TIME > DATE_ADD((CASE WHEN HOUR(DATE_SUB(NOW(),INTERVAL (SELECT SERVER_TIME_DIFF FROM live5.live1_time_diff) HOUR)) < 23 THEN DATE_FORMAT(DATE_SUB(DATE_SUB(NOW(),INTERVAL (SELECT SERVER_TIME_DIFF FROM live5.live1_time_diff) HOUR),INTERVAL 1 DAY),'%Y-%m-%d 23:00:00') ELSE DATE_FORMAT(DATE_SUB(NOW(),INTERVAL (SELECT SERVER_TIME_DIFF FROM live5.live1_time_diff) HOUR),'%Y-%m-%d 23:00:00') END),INTERVAL 1 HOUR))) THEN (mt4_trades.PROFIT+mt4_trades.SWAPS)*(SELECT AVERAGE FROM daily_prices WHERE SYMBOL LIKE 'GBPUSD' ORDER BY TIME DESC LIMIT 1) 
