WHEN (mt4_trades.`GROUP` IN (SELECT `GROUP` FROM live5.group_table WHERE BOOK = 'A' AND CURRENCY = 'AUD') AND (mt4_trades.CLOSE_TIME > DATE_ADD((CASE WHEN HOUR(DATE_SUB(NOW(),INTERVAL (SELECT SERVER_TIME_DIFF FROM live5.live1_time_diff) HOUR)) < 23 THEN DATE_FORMAT(DATE_SUB(DATE_SUB(NOW(),INTERVAL (SELECT SERVER_TIME_DIFF FROM live5.live1_time_diff) HOUR),INTERVAL 1 DAY),'%Y-%m-%d 23:00:00') ELSE DATE_FORMAT(DATE_SUB(NOW(),INTERVAL (SELECT SERVER_TIME_DIFF FROM live5.live1_time_diff) HOUR),'%Y-%m-%d 23:00:00') END),INTERVAL 1 HOUR))) THEN (mt4_trades.PROFIT+mt4_trades.SWAPS)*(SELECT AVERAGE FROM daily_prices WHERE SYMBOL LIKE 'AUDUSD' ORDER BY TIME DESC LIMIT 1) 
WHEN (mt4_trades.`GROUP` IN (SELECT `GROUP` FROM live5.group_table WHERE BOOK = 'A' AND CURRENCY = 'SGD') AND (mt4_trades.CLOSE_TIME > DATE_ADD((CASE WHEN HOUR(DATE_SUB(NOW(),INTERVAL (SELECT SERVER_TIME_DIFF FROM live5.live1_time_diff) HOUR)) < 23 THEN DATE_FORMAT(DATE_SUB(DATE_SUB(NOW(),INTERVAL (SELECT SERVER_TIME_DIFF FROM live5.live1_time_diff) HOUR),INTERVAL 1 DAY),'%Y-%m-%d 23:00:00') ELSE DATE_FORMAT(DATE_SUB(NOW(),INTERVAL (SELECT SERVER_TIME_DIFF FROM live5.live1_time_diff) HOUR),'%Y-%m-%d 23:00:00') END),INTERVAL 1 HOUR))) THEN (mt4_trades.PROFIT+mt4_trades.SWAPS)/(SELECT AVERAGE FROM daily_prices WHERE SYMBOL LIKE 'USDSGD' ORDER BY TIME DESC LIMIT 1) ELSE 0 END),2) AS Daily_PROFIT 
FROM live5.mt4_trades,live5.mt4_users,live5.mt4_prices WHERE mt4_trades.LOGIN = mt4_users.LOGIN AND mt4_trades.`GROUP` NOT LIKE '0%' AND CONCAT((CASE WHEN SUBSTRING_INDEX(mt4_trades.SYMBOL,'.',2) LIKE '.%y' THEN SUBSTRING_INDEX(SUBSTRING_INDEX(mt4_trades.SYMBOL,'.',2),'y',1) WHEN SUBSTRING_INDEX(mt4_trades.SYMBOL,'.',2) LIKE '.%q' THEN SUBSTRING_INDEX(SUBSTRING_INDEX(mt4_trades.SYMBOL,'.',2),'q',1) 
