AND LENGTH(mt4_trades.SYMBOL)>0 AND mt4_trades.CMD <2 AND mt4_trades.`GROUP` IN (SELECT `GROUP` FROM live5.group_table WHERE BOOK = 'A' AND LIVE = 'live5') GROUP BY (CASE WHEN SUBSTRING_INDEX(mt4_trades.SYMBOL,'.',2) LIKE '.%y' THEN SUBSTRING_INDEX(SUBSTRING_INDEX(mt4_trades.SYMBOL,'.',2),'y',1) WHEN SUBSTRING_INDEX(mt4_trades.SYMBOL,'.',2) LIKE '.%q' THEN SUBSTRING_INDEX(SUBSTRING_INDEX(mt4_trades.SYMBOL,'.',2),'q',1) 
WHEN SUBSTRING_INDEX(mt4_trades.SYMBOL,'.',2) LIKE '.%`' THEN SUBSTRING_INDEX(SUBSTRING_INDEX(mt4_trades.SYMBOL,'.',2),'`',1) WHEN SUBSTRING_INDEX(mt4_trades.SYMBOL,'.',2) LIKE '.%' THEN SUBSTRING_INDEX(mt4_trades.SYMBOL,'.',2) ELSE LEFT(mt4_trades.SYMBOL,6) END))
) AS B LEFT JOIN live5.mt4_prices ON B.CONVT_SYMBOL = mt4_prices.SYMBOL LEFT JOIN live5.mt4_symbols_update ON CONCAT(B.SYMBOL,'q') = mt4_symbols_update.SYMBOL GROUP BY B.SYMBOL)) AS X GROUP BY SYMBOL ORDER BY ABS(SUM(NET_VOL)) DESC,ABS(SUM(SUM_REVENUE)) DESC;";

